#Contract Address of Token we want to buy

def buy():
    tokenToBuy = web3.toChecksumAddress(input("Enter TokenAddress: "))          
    spend = web3.toChecksumAddress("0xc778417e063141139fce010982780140aa0cd5ab")   #Weth
    
    nonce = web3.eth.get_transaction_count(sender_address) # transactions #
    
    path = [spend, tokenToBuy]
    deadline = int(time.time()) + 10000
    # The Exchange function
    uni_txn = contract.functions.swapExactETHForTokens(
    0, # specify minimum amount of tokeny you want to receive 
    path,
    sender_address,
    deadline
    ).buildTransaction({
    'from': sender_address,
    'value': web3.toWei(0.001,'ether'),#This is the Token(WETH) amount you want to Swap from
    'gas': 250000,
    'gasPrice': web3.toWei('5','gwei'),
    'nonce': nonce,
    })

# MetaMask Confirmation
    signed_txn = web3.eth.account.sign_transaction(uni_txn, private_key=config.private)
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print(f"https://rinkeby.etherscan.io/tx/{web3.toHex(tx_token)}.")
#Loop too Invest X amount of Time
